import React, { Component } from "react";
import countryData from "./countries";
import Tab from "react-bootstrap/Tab";
import Tabs from "react-bootstrap/Tabs";
import Loading from "./Loading";
import { connect } from "react-redux";
import { setData } from "../redux/actions";

const MENA = countryData[1];
const allCountries = countryData[3];
const dictionary = countryData[4];

class Counter extends Component {
  state = {
    data: null,
    dataCountries: null,
    region: true,
    loading: true,
  };

  //   componentDidUpdate(prevProps) {
  //     if (prevProps.loading !== this.props.loading) {
  //       this.setState({ loading: !this.props.loading });
  //     }
  //   }

  changeRegion = () => {
    this.setState({ region: !this.state.region });
  };

  render() {
    let confirmedRows = <></>;
    let deadRows = <></>;
    let recoveredRows = <></>;
    let confirmedRowsMENA = <></>;
    let deadRowsMENA = <></>;
    let recoveredRowsMENA = <></>;

    const confirmedData = this.props.data[0];
    const deathsData = this.props.data[1];
    const recoveredData = this.props.data[2];

    confirmedRows = confirmedData.map((country) => (
      <tr>
        <td>
          &nbsp;&nbsp;&nbsp;
          <font style={{ color: "red", fontSize: "17px" }}>
            {country.confirmed}
          </font>
          <font style={{ fontSize: "15px" }}>
            &nbsp;&nbsp;
            {country.name}
          </font>
        </td>
      </tr>
    ));
    deadRows = deathsData.map((country) => (
      <tr>
        <td>
          &nbsp;&nbsp;&nbsp;
          <font style={{ color: "red", fontSize: "17px" }}>
            {country.deaths}
          </font>
          <font style={{ fontSize: "15px" }}>
            &nbsp;&nbsp;
            {country.name}
          </font>
        </td>
      </tr>
    ));
    recoveredRows = recoveredData.map((country) => (
      <tr>
        <td>
          &nbsp;&nbsp;&nbsp;
          <font style={{ color: "red", fontSize: "17px" }}>
            {country.recovered}
          </font>
          <font style={{ fontSize: "15px" }}>
            &nbsp;&nbsp;
            {country.name}
          </font>
        </td>
      </tr>
    ));
    var menaConfirmed = 0,
      menaDeaths = 0,
      menaRecovered = 0;

    confirmedRowsMENA = confirmedData.map((country) => {
      if (MENA.indexOf(country.name) !== -1) {
        menaConfirmed += country.confirmed;
        return (
          <tr>
            <td>
              &nbsp;&nbsp;&nbsp;
              <font
                style={{
                  color: "red",
                  fontSize: "17px",
                }}
              >
                {country.confirmed}
              </font>
              <font style={{ fontSize: "15px" }}>
                &nbsp;&nbsp;
                {country.name}
              </font>
            </td>
          </tr>
        );
      }
    });
    deadRowsMENA = deathsData.map((country) => {
      if (MENA.indexOf(country.name) !== -1) {
        menaDeaths += country.deaths;
        return (
          <tr>
            <td>
              &nbsp;&nbsp;&nbsp;
              <font
                style={{
                  color: "red",
                  fontSize: "17px",
                }}
              >
                {country.deaths}
              </font>
              <font style={{ fontSize: "15px" }}>
                &nbsp;&nbsp;
                {country.name}
              </font>
            </td>
          </tr>
        );
      }
    });
    recoveredRowsMENA = recoveredData.map((country) => {
      if (MENA.indexOf(country.name) !== -1) {
        menaRecovered += country.recovered;
        return (
          <tr>
            <td>
              &nbsp;&nbsp;&nbsp;
              <font
                style={{
                  color: "red",
                  fontSize: "17px",
                }}
              >
                {country.recovered}
              </font>
              <font style={{ fontSize: "15px" }}>
                &nbsp;&nbsp;
                {country.name}
              </font>
            </td>
          </tr>
        );
      }
    });

    if (this.props.loading) {
      return <Loading />;
    }
    return (
      <div className="counter-div">
        {" "}
        <div class="custom-control custom-switch">
          <input
            type="checkbox"
            class="custom-control-input"
            onClick={() => this.changeRegion()}
            id="regionswitch"
          />
          <label class="custom-control-label" for="regionswitch">
            {this.state.region ? "Show Global Data" : "Show Arab World"}
          </label>
        </div>{" "}
        <br />
        <div className="container counter-nav">
          <Tabs defaultActiveKey="confirmed" id="counter">
            <Tab eventKey="confirmed" title="Confirmed">
              <div className="card mx-auto counter">
                <h1 className="display-3" style={{ color: "red" }}>
                  {this.props.loading
                    ? ""
                    : this.state.region
                    ? menaConfirmed
                    : this.state.data.confirmed.value}
                </h1>
              </div>
              <div className="card mx-auto counter list my-custom-scrollbar">
                <table class="table-dark ">
                  <tbody>
                    {this.props.loading
                      ? ""
                      : this.state.region
                      ? confirmedRowsMENA
                      : confirmedRows}
                  </tbody>
                </table>
              </div>
              <br />
            </Tab>
            <Tab eventKey="deaths" title="Deaths">
              <div className="card mx-auto counter">
                <h1 className="display-3" style={{ color: "red" }}>
                  {this.props.loading
                    ? ""
                    : this.state.region
                    ? menaDeaths
                    : this.state.data.deaths.value}
                </h1>
              </div>
              <div className="card mx-auto counter list my-custom-scrollbar">
                <table class="table-dark ">
                  <tbody>
                    {" "}
                    {this.props.loading
                      ? ""
                      : this.state.region
                      ? deadRowsMENA
                      : deadRows}
                  </tbody>
                </table>
              </div>
              <br />
            </Tab>
            <Tab eventKey="recovered" title="Recovered">
              <div className="card mx-auto counter">
                <h1 className="display-3" style={{ color: "red" }}>
                  {this.props.loading
                    ? ""
                    : this.state.region
                    ? menaRecovered
                    : this.state.data.recovered.value}
                </h1>
              </div>
              <div className="card mx-auto counter list my-custom-scrollbar">
                <table class="table-dark ">
                  <tbody>
                    {" "}
                    {this.props.loading
                      ? ""
                      : this.state.region
                      ? recoveredRowsMENA
                      : recoveredRows}
                  </tbody>
                </table>
              </div>
              <br />
            </Tab>
          </Tabs>
          <br /> <br /> <br /> <br />
        </div>
      </div>
    );
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    setData: (data) => dispatch(setData(data)),
  };
};

export default connect(
  null,
  mapDispatchToProps
)(Counter);
