import React, { Component } from "react";
import Plot from "react-plotly.js";
import colormap from "colormap";
import countryData from "./countries";
import Loading from "./Loading";

const countries = countryData[1];

class PieChart extends Component {
  state = {
    data: null,
    selector: "confirmed",
  };

  componentDidUpdate(prevProps) {
    if (prevProps.loading !== this.props.loading) {
      this.setState({ loading: !this.props.loading });
    }
  }

  handleOnClick = (selected) => {
    this.setState({ selector: selected });
  };

  render() {
    const selectors = ["confirmed", "deaths", "recovered"];

    const buttons = selectors.map((selector) => (
      <button
        className={
          this.state.selector === selector
            ? "btn btn-outline-dark clicked"
            : "btn btn-outline-dark"
        }
        onClick={() => this.handleOnClick(selector)}
      >
        {selector}
      </button>
    ));

    const confirmedData = this.props.data[0];
    const deathsData = this.props.data[1];
    const recoveredData = this.props.data[2];

    var confirmedX,
      confirmedY = [];
    confirmedData.map((country) => {
      confirmedX.push(country.name);
      confirmedY.push(country.confirmed);
    });

    var deathsX,
      deathsY = [];
    deathsData.map((country) => {
      deathsX.push(country.name);
      deathsY.push(country.deaths);
    });

    var recoveredX,
      recoveredY = [];
    recoveredData.map((country) => {
      recoveredX.push(country.name);
      recoveredY.push(country.recovered);
    });

    if (this.state.selector === "confirmed") {
      var data = [
        {
          values: confirmedY,
          labels: confirmedX,
          type: "pie",
          textinfo: "label",
          title: {
            text: "Total Confirmed <br> ",
            position: "  center",
          },
        },
      ];
    } else if (this.state.selector === "deaths") {
      var data = [
        {
          values: deathsX,
          labels: deathsY,
          type: "pie",
          textinfo: "label",
          title: { text: "Total Deaths <br> ", position: "  center" },
        },
      ];
    } else {
      var data = [
        {
          values: recoveredX,
          labels: recoveredY,
          type: "pie",
          textinfo: "label",
          title: { text: "Total Recovered <br> ", position: "  center" },
        },
      ];
    }

    const colorway = colormap({
      colormap: "jet",
      nshades: countries.length,
      format: "hex",
      alpha: 1,
    });
    if (this.state.loading) {
      return <Loading />;
    }
    return (
      <div style={{ width: "100%", height: "100%" }}>
        {buttons}
        <Plot
          data={data}
          layout={{
            modebar: {
              bgcolor: "rgba(255,255,255,0)",
            },
            font: { color: "#99aab5", size: 14 },
            showlegend: false,

            colorway: colorway.reverse(),
            plot_bgcolor: "#161616",
            paper_bgcolor: "#161616",
            autosize: true,
          }}
          config={{
            modeBarButtonsToRemove: [
              "toggleSpikelines",
              "zoomIn2d",
              "zoomOut2d",
              "autoScale2d",
              "hoverClosestCartesian",
              "hoverCompareCartesian",
              "hoverClosestPie",
            ],
            displaylogo: false,
          }}
          useResizeHandler={true}
          style={{ width: "100%", height: "100%" }}
        />{" "}
      </div>
    );
  }
}

export default PieChart;
