import React, { Component } from "react";
import { Container } from "./styled-components";
import countryData from "./countries";
import axios from "axios";
import Loading from "./Loading";

import Counter from "./Counter";
const MENA = countryData[0];
const dictionary = countryData[4];

const instance1 = axios.create({
  baseURL: "https://covid19.mathdro.id/api/",
  loading: true,
});

const instance2 = axios.create({
  baseURL: "https://covidapi.info/api/v1/global/latest",
  loading: true,
});

class App extends Component {
  state = {
    data: null,
    dataCountries: null,
    loading: true,
    region: true,
  };

  fetchData = async () => {
    try {
      let response1 = await instance1.get(``);
      let data1 = response1.data;
      let response2 = await instance2.get(``);
      let data2 = response2.data;
      this.setState({ data: data1, dataCountries: data2, loading: false });
    } catch (error) {
      console.error(error);
    }
  };

  componentDidMount = async () => {
    this.fetchData();
  };

  sortCountries = (items, region) => {
    let cleanedConfirmed = [],
      cleanedRecovered = [],
      cleanedDeaths = [];
    if (region) {
      items.map((country) => {
        let countryName = Object.keys(country)[0];
        console.log(MENA);
        if (MENA.indexOf(countryName) !== -1) {
          let confirmedObj = {
            name: dictionary[countryName],
            confirmed: country[countryName].confirmed,
          };
          let recoveredObj = {
            name: dictionary[countryName],
            recovered: country[countryName].recovered,
          };
          let deathsObj = {
            name: dictionary[countryName],
            deaths: country[countryName].deaths,
          };
          cleanedConfirmed.push(confirmedObj);
          cleanedRecovered.push(recoveredObj);
          cleanedDeaths.push(deathsObj);
        }
      });
    } else {
      items.map((country) => {
        let countryName = Object.keys(country)[0];
        let confirmedObj = {
          name: dictionary[countryName],
          confirmed: country[countryName].confirmed,
        };
        let recoveredObj = {
          name: dictionary[countryName],
          recovered: country[countryName].recovered,
        };
        let deathsObj = {
          name: dictionary[countryName],
          deaths: country[countryName].deaths,
        };
        cleanedConfirmed.push(confirmedObj);
        cleanedRecovered.push(recoveredObj);
        cleanedDeaths.push(deathsObj);
      });
    }
    const confirmed = cleanedConfirmed.sort(
      (a, b) => b.confirmed - a.confirmed
    );
    const recovered = cleanedRecovered.sort(
      (a, b) => b.recovered - a.recovered
    );
    const deaths = cleanedDeaths.sort((a, b) => b.deaths - a.deaths);
    return [[confirmed], [recovered], [deaths]];
  };

  render() {
    if (!this.state.loading) {
      const sortedData = this.sortCountries(
        this.state.dataCountries.result,
        true
      );
      console.log(sortedData);
      const confirmedData = sortedData[0][0];
      const deathsData = sortedData[1][0];
      const recoveredData = sortedData[2][0];

      const sortedDataMENA = this.sortCountries(
        this.state.dataCountries.result,
        false
      );
      console.log(sortedData);
      const confirmedDataMENA = sortedDataMENA[0][0];
      const deathsDataMENA = sortedDataMENA[1][0];
      const recoveredDataMENA = sortedDataMENA[2][0];

      return (
        <Container>
          <button
            className="btn btn-seconday"
            onClick={() => this.setState({ region: !this.state.region })}
          >
            change region
          </button>
          {/* content area start */}
          <Container className="container-fluid pr-5 pl-5 pt-5 pb-5">
            {/* row 1  */}
            <Container className="row">
              {/* counter  */}
              <Container className="col-lg-2 col-sm-6 is-light-text mb-4">
                <Container className="card grid-card is-card-dark">
                  <Container className="card-heading">
                    <Container className="is-dark-text-light letter-spacing text-small" />
                  </Container>

                  <Counter
                    data={
                      this.state.region
                        ? [confirmedDataMENA, deathsDataMENA, recoveredDataMENA]
                        : [confirmedData, deathsData, recoveredData]
                    }
                    loading={this.state.loading}
                  />

                  <Container className="card-value pt-4 text-x-large">
                    <span className="text-large pr-1" />
                  </Container>
                </Container>
              </Container>
              {/* counter list  */}
              <Container className="col-lg-2 col-sm-6 is-light-text mb-4">
                <Container className="card grid-card is-card-dark">
                  <Container className="card-heading">
                    <Container className="is-dark-text-light letter-spacing text-small" />
                  </Container>

                  <Container className="card-value pt-4 text-x-large">
                    <span className="text-large pr-1" />
                  </Container>
                </Container>
              </Container>
              {/* timegraph  */}
              <Container className="col-lg-5 col-sm-6 is-light-text mb-4">
                <Container className="card grid-card is-card-dark">
                  <Container className="card-heading">
                    <Container className="is-dark-text-light letter-spacing text-small">
                      small
                    </Container>
                    <Container className="card-heading-brand">
                      <i className="fab fa-amazon text-large" />
                    </Container>
                  </Container>

                  <Container className="card-value pt-4 text-x-large">
                    <span className="text-large pr-1" />
                  </Container>
                </Container>
              </Container>
              {/* pie chart  */}
              <Container className="col-lg-3 col-sm-6 is-light-text mb-4">
                <Container className="card grid-card is-card-dark">
                  <Container className="card-heading">
                    <Container className="is-dark-text-light letter-spacing text-small">
                      small
                    </Container>
                    <Container className="card-heading-brand">
                      <i className="fab fa-etsy text-medium" />
                    </Container>
                  </Container>

                  <Container className="card-value pt-4 text-x-large">
                    <span className="text-large pr-1">$</span>
                  </Container>
                </Container>
              </Container>
            </Container>

            {/* row 2  */}

            <Container className="row" style={{ minHeight: "500px" }}>
              <Container className="col-md-4 col-lg-9 is-light-text mb-4 ">
                <Container className="card is-card-dark map-card">
                  big
                </Container>
              </Container>
              <Container className="col-lg-3 col-sm-6 is-light-text mb-4">
                <Container className="card grid-card is-card-dark">
                  <Container className="card-heading">
                    <Container className="is-dark-text-light letter-spacing text-small">
                      small
                    </Container>
                    <Container className="card-heading-brand">
                      <i className="fab fa-etsy text-medium" />
                    </Container>
                  </Container>

                  <Container className="card-value pt-4 text-x-large">
                    <span className="text-large pr-1">$</span>
                  </Container>
                </Container>
              </Container>
            </Container>

            {/* footer */}
            <Container className="row">
              <Container className="col-md-4 col-lg-3 is-light-text mb-4">
                <Container className="card footer-card is-card-dark">
                  <Container className="card-heading mb-3">small</Container>
                </Container>
              </Container>

              <Container className="col-md-8 col-lg-9 is-light-text mb-4">
                <Container className="card is-card-dark footer-card">
                  footer
                </Container>
              </Container>
            </Container>
          </Container>
        </Container>
      );
    } else {
      return (
        <div>
          {" "}
          <Loading />
        </div>
      );
    }
  }
}

export default App;
