import React, { useState } from "react";
import Plot from "react-plotly.js";
import countryData from "./countries";

const reverseDict = countryData[5];

const MapChart = (props) => {
  let confirmedList = [],
    countryNames = [];
  props.data.map((country) => {
    confirmedList.push(country.confirmed);
    countryNames.push(reverseDict[country.name]);
  });

  let scaledData = confirmedList.map((num) => Math.pow(num, 0.3));
  let data = [
    {
      type: "scattergeo",
      mode: "markers",
      locations: countryNames,
      hoverinfo: "text, label",
      text: confirmedList,
      marker: {
        size: scaledData,
        color: "rgba(255,0,0,0.75)",
        sizeref: 1,
        line: {
          color: "black",
        },
      },
      name: "europe data",
    },
  ];
  if (props.loading) {
    return <Loading />;
  }
  return (
    <div>
      <Plot
        data={data}
        layout={{
          modebar: { bgcolor: "rgba(255,255,255,0)" },

          geo: {
            scope: "World",
            resolution: "200",
            showland: true,
            landcolor: "#0f1316",
            showframe: true,
            bgcolor: "#0f1316",
            showcountries: true,
            showocean: true,
            oceancolor: "#030217",
          },
          height: "420",
          border: "solid",
          margin: { l: 0, r: 0, t: 0, b: 0, pad: 0 },
          plot_bgcolor: "rgba(0,0,0,0)",
          paper_bgcolor: "rgba(0,0,0,0)",
          autosize: true,
        }}
        useResizeHandler={true}
        style={{ width: "100%", height: "100%" }}
        config={
          {
            modeBarButtonsToRemove: [
              "toggleSpikelines",
              "autoScale2d",
              "hoverClosestCartesian",
              "hoverCompareCartesian",
              "select2d",
              "lasso2d",
              "hoverClosestGeo",
              "zoomInGeo",
              "zoomOutGeo",
            ],
            displaylogo: false,
          } // onClick={(data) => handleShow(data)}
        }
      />
    </div>
  );
};

export default MapChart;
